package basic;

public class Logical {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String username="abc";
		String password="abcd";
		System.out.println(username=="abc" && password=="abcd");
		System.out.println(username=="abc" || password=="abcd");
		
		System.out.println(!(username=="abc"));
		
		
		

	}

}
