package basic;

public class nested_if {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int age=25;
		int weight=100;
		if(age>=10 && weight>50)
		{
			if(age>20)
			{
				System.out.println("abc");
			}
			else
			{
				System.out.println("xyz");
			}
		}
		
		
	}

}
