package basic;

public class while_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=1;
		while(i<=10)
		{
			System.out.println(i);
			i++;
		}
		System.out.println("----------");
		int a=10;
		while(a>=1)
		{
			System.out.println(a);
			a--;
		}

	}

}
